import React, { Component } from "react";

// class Counter extends Component {
//   render() {
//     return <h1>hello</h1>;
//   }
// }

// export default Counter;
class Counter extends Component {
  state = {
    count: 0,
  };
  render() {
    return (
      <div>
        <h1>{this.state.count}</h1>
        <button>Increment</button>
      </div>
    );
  }
  formatCount() {
    return this.state.count === 0 ? "zero" : this.state.count;
  }
}

export default Counter;
