import React, { Component } from "react";

// class Counter extends Component {
//   render() {
//     return <h1>hello</h1>;
//   }
// }

// export default Counter;
class Counter extends Component {
  state = {
    count: 2,
  };
  styles = {
    fontSize: 50,
    fontWeight: "bold",
    padding: 30,
  };
  render() {
    let dynamicClasses = " ";
    // dynamicClasses += this.state.count === 0 ? "warning" : "primary";
    if (this.state.count === 0) {
      dynamicClasses = "badge badge-warning m-2 ";
    } else {
      dynamicClasses = "badge badge-primary m-2 ";
    }

    return (
      <div>
        <span style={this.styles} className={dynamicClasses}>
          {this.formatCount()}
        </span>
        <button className="btn btn-secondary btn-sm">Increment</button>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  }
}
export default Counter;
