import React, { Component } from "react";

// class Counter extends Component {
//   render() {
//     return <h1>hello</h1>;
//   }
// }

// export default Counter;
class Counter extends Component {
  state = {
    count: 0,
  };
  styles = {
    fontSize: 20,
    fontWeight: "bold",
  };
  buttonStyle = {
    position: "fabsolute",
  };

  increment = () => {
    this.setState({ count: this.state.count + 1 });
  };
  //   constructor() {
  //     super();
  //     this.increment = this.increment.bind(this);
  //   }

  render() {
    let dynamicClasses = " ";
    // dynamicClasses += this.state.count === 0 ? "warning" : "primary";
    if (this.state.count === 0) {
      dynamicClasses = "badge badge-warning m-2 ";
    } else {
      dynamicClasses = "badge badge-primary m-2 ";
    }

    return (
      <div>
        <span style={this.styles} className={dynamicClasses}>
          {this.formatCount()}
        </span>
        <button
          style={this.buttonStyle}
          onClick={this.increment}
          className="btn btn-secondary btn-sm"
        >
          Increment
        </button>
      </div>
    );
  }
  formatCount() {
    const { count } = this.state;
    return count === 0 ? "zero" : count;
  }
}
export default Counter;
